import React, { useEffect, useRef, useState } from 'react';
import './styles.css'; 
import Marquee from './components/Marquee'; // Adjust the path according to your file structure
import Navbar from './components/Navbar'; // Adjust the path according to your file structure
import Intro from './components/Intro'; // Adjust the path according to your file structure
import About from './components/About'; // Adjust the path according to your file structure

import './App.css'; // Adjust the path according to your file structure

const App = () => {
  const navbarRef = useRef(null);
  const marqueeRef = useRef(null);
  const [lastScrollTop, setLastScrollTop] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScroll = window.scrollY || document.documentElement.scrollTop;
      const marquee = marqueeRef.current;
      const navbar = navbarRef.current;

      if (currentScroll > lastScrollTop && navbar && marquee) {
        navbar.classList.add('navbar-hidden');
        marquee.classList.add('marquee-hidden');
      } else if (currentScroll < lastScrollTop && navbar && marquee) {
        navbar.classList.remove('navbar-hidden');
        marquee.classList.remove('marquee-hidden');
      }

      setLastScrollTop(currentScroll);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollTop]);

  useEffect(() => {
    const links = document.querySelectorAll('a');
    links.forEach(link => {
      link.target = '_blank';
    });

    const overrides = document.getElementsByClassName('link_override');
    for (let i = 0; i < overrides.length; i++) {
      overrides[i].target = '_self';
    }
  }, []);


  return (
    <div className="App">
      <Marquee ref={marqueeRef} />
      <Navbar ref={navbarRef} />
      <Intro/>
    </div>
  );
};

export default App;
